import React, { useState } from 'react';
import './content.css';

import DatePicker from 'react-datepicker';
import { registerLocale } from "react-datepicker";
import fi from 'date-fns/locale/fi';
import "react-datepicker/dist/react-datepicker.css";

import moment from 'moment';

import InputRange from 'react-input-range';
import "react-input-range/lib/css/index.css";

import { useForm } from "react-hook-form";

registerLocale('fi', fi);


function Formbox() {
  const { register, handleSubmit, watch, errors } = useForm();


  //Formissa kerättävä data:
  let [rangevalue, setRangevalue] = useState(1);

  let [tosleepvalue, setTosleepvalue] = useState(new Date());
  let formattedtosleepvalue = moment(tosleepvalue).format("DD.MM.YYYY HH:mm");

  let [fromsleepvalue, setFromsleepvalue] = useState(new Date());
  let formattedfromsleepvalue = moment(fromsleepvalue).format("DD.MM.YYYY HH:mm");

  let [desc, setDesc] = useState();

  // Nukkumiseen käytetty aika selvitettävä, jotta voi
  let totalsleeptime = String(fromsleepvalue - tosleepvalue);


  const onSubmit = data => {
    data.tosleepvalue = formattedtosleepvalue;
    data.fromsleepvalue = formattedfromsleepvalue;
    data.rangevalue = parseInt(data.rangevalue);

    console.log('submittia = ' + JSON.stringify(data));


  }

  return (
    <div className="box">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Kävin nukkumaan:</label>
        <DatePicker
          selected={tosleepvalue}
          onChange={e => setTosleepvalue(e)}
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"
          timeIntervals={15}
          ref={register({ name: 'tosleepvalue', value: formattedtosleepvalue })}
        />

        <label>Heräsin:</label>
        <DatePicker
          selected={fromsleepvalue}
          onChange={e => setFromsleepvalue(e)}
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"
          timeIntervals={15}
          ref={register({ name: 'fromleepvalue', value: formattedfromsleepvalue })}
        />
        <br></br>
        <label>Unen laatu:</label>
        <InputRange
          value={rangevalue}
          onChange={e => setRangevalue(e)}
          maxValue={5}
          minValue={1}
          ref={register({ name: 'range', value: rangevalue })}
        />

        <label>Sanallinen arvio:</label><br></br>
        <textarea name="desc" onChange={(e) => setDesc(e)}></textarea>

        <button type="submit">Tallenna</button>

      </form>


    </div>
  );
}

export default Formbox;
