import React, { useState, useEffect } from 'react';
import './content.css';

// React-tabs Module
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Cell, Legend } from 'recharts';

function Databox() {

  let [Email, setEmail] = useState('eerokos');
  const [dataset, setDataset] = useState([]);

  useEffect(() => {
    getData();    
  }, []);

  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { setDataset(data); })
      .catch(error => console.error('Error:', error))
  }


  let bars= [];
  const fillBars = () => {
    console.log('filling bars');
    return dataset.map((label, index) => (
      <Bar key={label} dataKey={label} fill="#8884d8" />
    ));
  }


  return (
    <Tabs className="box">
      <TabList>
        <Tab>Array</Tab>
        <Tab>Graph</Tab>
      </TabList>

      <TabPanel>
        <table>
          <thead>
            <tr>
              <th>Tosleep</th>
              <th>Fromsleep</th>
              <th>TotalHours</th>
              <th>Range</th>
            </tr>
          </thead>
          <tbody>
            {dataset.map((c, index) =>
              <tr key={c.id}>
                <td>{c.tosleep}</td>
                <td>{c.fromsleep}</td>
                <td>{c.totalHours}</td>
                <td>{c.range}</td>
              </tr>
            )}
          </tbody>
        </table>
      </TabPanel>
      <TabPanel>
        <BarChart width={450} height={250} data={dataset}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey={dataset.id} />
          <YAxis dataKey={dataset.totalHours} />
          <Tooltip />
          <Legend />
          {fillBars()}
        </BarChart>
      </TabPanel>
    </Tabs>
  );
}

export default Databox;
