import React, { useState, useEffect } from 'react';
import './content.css';

import { useForm } from "react-hook-form"; // Form validation module

function Login({setToken}) {

    const { register, handleSubmit, watch, errors, reset } = useForm();

    let [username, setUsername] = useState('');
    let [passwrd, setPasswrd] = useState('');


    const onSubmit = data => {
        data.username = username;
        data.passwrd = passwrd;
        console.log(data);

        auth();
        function auth(){
            if(data.username === 'eerokos@uef.fi' && data.passwrd === '123'){
                setToken(true);                
            }
        }
    }

    return (
        <div className="box">
            <form onSubmit={handleSubmit(onSubmit)}>
                <label>Käyttäjätunnus:</label>
                <input
                    placeholder="@UEF.fi-sähköposti"
                    type="text"
                    onChange={(e) => setUsername(e.target.value)}
                    value={username}
                    ref={register({name: 'username', value: username})}></input>
                <br></br>
                <label>Salasana:</label>
                <input
                    type="password"
                    onChange={(e) => setPasswrd(e.target.value)}
                    value={passwrd}
                    ref={register({name: 'passwrd', value: passwrd})}></input>
                    <br></br>
                <button type="submit">Kirjaudu Sisään</button>
            </form>
        </div>
    );
}

export default Login;
