import React, { useState } from 'react';
import './content.css';

import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Cell } from 'recharts';

function Databox() {

  let [Email, setEmail] = useState('eerokos');
  let [data, setData] = useState([]);

  useEffect(() => {
    getData();

  }, []);

  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(response => response.json())
      .catch(error => console.error('Error:', error))
      .then(response => { setData(JSON.stringify(response)) });
  }

  return (
    <div className="box">
      <h1>Data</h1>
      <p>{data}</p>

      <BarChart width="100%" height="100%" data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis type="category" dataKey="id" />
        <YAxis type="number" />
        <Tooltip />
        <Bar dataKey="value" fill="#28A745">
          {
            data.map((entry, index) => <Cell label={(e) => e.totalHours} key={index} />)
          }
        </Bar>
      </BarChart>

    </div>
  );
}

export default Databox;
