import React, { useState, useEffect } from 'react';
import './content.css';

// React-tabs module
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

// Recharts.js module
import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Cell, Legend } from 'recharts';

function Databox({isupdated}) {

  let [Email, setEmail] = useState('eerokos');
  const [dataset, setDataset] = useState([]);

  useEffect(() => {
    getData();
  }, [isupdated]);

  // Fetching data from the server
  async function getData() {    
    fetch('http://localhost:3000/' + Email)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { setDataset(data); console.log(data); })
      .catch(error => console.error('Error:', error))
  }

  async function removeObs(obs) {    
    await fetch('http://localhost:3000/' + Email +'/'+ obs.id, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },      
    })
      .then(() => { getData(); })
      .catch(error => console.error("error: " + error))
  }

  function modifyObs(c){
    console.log('modify with id: ' +c.id);
    // TODO:
    // Send the id to Formbox -> fetch data with id to form -> PUSH instead of POST
  }



  return (
    <Tabs className="box">
      <TabList>
        <Tab>Array</Tab>
        <Tab>Graph</Tab>
      </TabList>

      <TabPanel>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tosleep</th>
              <th>Fromsleep</th>
              <th>TotalHours</th>
              <th>Range</th>
            </tr>
          </thead>
          <tbody>
            {dataset.map((c, index) =>
              <tr key={c.id}>
                <td>{c.id}</td>
                <td>{c.tosleep}</td>
                <td>{c.fromsleep}</td>
                <td>{c.totalHours}</td>
                <td>{c.range}</td>
                <button onClick={() => modifyObs(c)}>Muokkaa</button>
                <button onClick={() => removeObs(c)}>Poista</button>
              </tr>)}
          </tbody>
        </table>
      </TabPanel>

      <TabPanel>
        <BarChart width={450} height={250} data={dataset}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="id" />
          <YAxis dataKey="totalHours" />
          <Tooltip />
          <Legend />

            {dataset.map((c, index) =>
              <Bar label={c.id} dataKey={c.totalHours} key={index}  />
            )}

        </BarChart>
      </TabPanel>


    </Tabs>
  );
}

export default Databox;
