import React, { useState } from 'react';
import './content.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import InputRange from 'react-input-range';
import "react-input-range/lib/css/index.css";

function Formbox() {
  //Formissa kerättävä data:
  const [rangevalue, setRangevalue] = useState(1);
  const [tosleepvalue, setTosleepvalue] = useState(); //Tallentuu muodossa 00:00:00
  const [fromsleepvalue, setFromsleepvalue] = useState(); //Tallentuu muodossa 00:00:00
  const [date, setDate] = useState();

  // Nukkumiseen käytetty aika selvitettävä, jotta voi
  const totalsleeptime = fromsleepvalue - tosleepvalue;


  return (
    <div className="box">
      <h1>Form</h1>

      <form>
        <select onChange={e => setDate(e.target.value)}>
          <option value="Maanantai" defaultValue>Maanantai</option>
          <option value="Tiistai">Tiistai</option>
          <option value="Keskiviikko">Keskiviikko</option>
          <option value="Torstai">Torstai</option>
          <option value="Perjantai">Perjantai</option>
          <option value="Lauantai">Lauantai</option>
          <option value="Sunnuntai">Sunnuntai</option>
        </select>

        <br></br>
        <label>Kävin nukkumaan klo:</label>
        <DatePicker
          showTimeSelect
          showTimeSelectOnly          
          timeFormat="HH:mm"
          timeIntervals={15}
          value={tosleepvalue}
          onChange={e => setTosleepvalue(e.toLocaleTimeString())}          
        />
        <label>Heräsin klo:</label>
        <DatePicker
          showTimeSelect
          showTimeSelectOnly
          timeFormat="HH:mm"
          timeIntervals={15}
          value={fromsleepvalue}
          onChange={e => setFromsleepvalue(e.toLocaleTimeString())}  
        />
        <br></br>
        <label>Unen laatu:</label>


        <InputRange
          maxValue={5}
          minValue={1}
          value={rangevalue}
          onChange={e => setRangevalue(e)}
        />

        <label>Sanallinen arvio:</label><br></br>
        <textarea onChange={()=>console.log(totalsleeptime)}></textarea>

        <button>Tallenna</button>  
      </form>


    </div>
  );
}

export default Formbox;
