import React, { useState, useEffect } from 'react';
import './content.css';


import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Cell } from 'recharts';

function Databox() {

  let [Email, setEmail] = useState('eerokos');
  let [data, setData] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(response => response.json())
      .catch(error => console.error('Error:', error))
      .then(response => { setData(JSON.stringify(response)) });
  }

  function renderTable() {
    return data.map((obs, index) => {
      const { id, tosleep, fromsleep, totalHours, range } = obs 
      return (
        <tr key={id}>
          <td>{id}</td>
          <td>{tosleep}</td>
          <td>{fromsleep}</td>
          <td>{totalHours}</td>
          <td>{range}</td>
        </tr>
      )
    })
  }

  function renderTableHeader() {

    return data.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })
  }



  return (
    <div className="box">
      <h1>Data</h1>      

      <table>
        <tbody>
          <tr>{renderTableHeader()}</tr>
          {renderTable()}
        </tbody>
      </table>

    </div>
  );
}

export default Databox;
