import React, { useState } from 'react';
import './App.css';
import './content.css';
import Formbox from './Formbox';
import Databox from './Databox';
import { useForm } from "react-hook-form"; // Form validation module

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";

function Login(token) {
  const { register, handleSubmit, watch, errors, reset } = useForm();

  let [username, setUsername] = useState('');
  let [passwrd, setPasswrd] = useState('');
  let history = useHistory();

  const onSubmit = data => {
    data.passwrd = passwrd;
    data.username = username.substring(0, username.indexOf('@'));
    console.log(data);

    auth();
    function auth() {



      if (data.username === 'eerokos' && data.passwrd === '123') {
        history.push('/')
      }
    }
  }


  // Fetching data from the server
  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { setDataset(data); setModifiedId(); }) // Set fetched data to an array
      .catch(error => console.error('Error:', error))
  }

  return (
    <div className="box">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Käyttäjätunnus:</label>
        <input
          placeholder="@UEF.fi-sähköposti"
          name="username"
          type="text"
          onChange={(e) => setUsername(e.target.value)}
          value={username}
          ref={register({ name: 'username', value: username })}
          ref={register({ required: true })}
        ></input>
        <br></br>
        <label>Salasana:</label>
        <input
          name="passwrd"
          type="password"
          onChange={(e) => setPasswrd(e.target.value)}
          value={passwrd}
          ref={register({ name: 'passwrd', value: passwrd })}
          ref={register({ required: true })}
        ></input>
        <br></br>
        <button type="submit">Kirjaudu</button>
      </form>
    </div>
  );
}

function App() {

  let [isupdated, setIsupdated] = useState(false); // For the components (Formbox & Databox) to know if the data has been updated
  let [modifiedId, setModifiedId] = useState(); // For the components to know what observation would the user want to modify
  let [token, setToken] = useState(false);

  return (
    <Router>
      <Switch>
        <Route path='/login'>
          <Login token={token} />
        </Route>
        <Route path='/'>
          <div className="container">
            <div className="header">
              <h4>Kansanterveystieteen perusteet -Unipäiväkirja</h4>
              <Link to="/login">Kirjaudu ulos</Link>
            </div>

            <div className="content">
              <Formbox setIsupdated={setIsupdated} modifiedId={modifiedId} Email={username} />
              <Databox isupdated={isupdated} setModifiedId={setModifiedId} Email={username}/>
            </div>

            <div className="footer"><h5>Footer</h5></div>
          </div>
        </Route>
      </Switch>
    </Router>
  )
}
export default App;
