import React, { useState } from 'react';
import './App.css';
import './content.css';
import './login.css';
import Formbox from './Formbox';
import Databox from './Databox';
import { useForm } from "react-hook-form"; // Form validation module

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";

function Login({Email, setEmail}) {
  const { register, handleSubmit, watch, errors, reset } = useForm();


  let [passwrd, setPasswrd] = useState('');
  let history = useHistory();

  const onSubmit = data => {
    data.passwrd = passwrd;
    Email = Email.substring(0, Email.indexOf('@'));  
    data.Email = Email;
    setEmail(Email);

    console.log(data);

    history.push('/')
  }

  return (
    <div className="loginbox">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Käyttäjätunnus:</label>
        <input
          placeholder="@UEF.fi-sähköposti"
          name="Email"
          type="text"
          onChange={(e) => setEmail(e.target.value)}
          value={Email}
          ref={register({ name: 'Email', value: Email })}
          ref={register({ required: true })}
        ></input>
        <br></br>
        <label>Salasana:</label>
        <input
          name="passwrd"
          type="password"
          onChange={(e) => setPasswrd(e.target.value)}
          value={passwrd}
          ref={register({ name: 'passwrd', value: passwrd })}
          ref={register({ required: true })}
        ></input>
        <br></br>
        <button type="submit">Kirjaudu</button>
      </form>
    </div>
  );
}



function App() {

  let [isupdated, setIsupdated] = useState(false); // For the components (Formbox & Databox) to know if the data has been updated
  let [modifiedId, setModifiedId] = useState(); // For the components to know what observation would the user want to modify

  let [Email, setEmail] = useState('');

  return (
    <Router>
      <Switch>
        <Route path='/login'>
          <Login Email={Email} setEmail={setEmail}/>
        </Route>
        <Route path='/'>
          <div className="container">
            <div className="header">
              <h4>Kansanterveystieteen perusteet -Unipäiväkirja</h4>
              <Link to="/login">Kirjaudu ulos</Link>
            </div>

            <div className="content">
              <Formbox setIsupdated={setIsupdated} modifiedId={modifiedId} Email={Email} />
              <Databox isupdated={isupdated} setModifiedId={setModifiedId} Email={Email}/>
            </div>

            <div className="footer"><h5>Footer</h5></div>
          </div>
        </Route>
      </Switch>
    </Router>
  )
}
export default App;
