import React, { useState, useEffect } from 'react';
import './content.css'; // Styling for Formbox & Databox components
import './App.css'; // Basic styling

import { FaEdit, FaTrashAlt } from "react-icons/fa";

// PNG DOWNLOAD
import { useRechartToPng } from "recharts-to-png";
import { saveAs } from 'file-saver';
// PNG DOWNLOAD

import Modal from 'react-modal';

// React-tabs module
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

import moment from 'moment'; // Module for date formatting

// Recharts.js module
import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Legend } from 'recharts';


function Databox({ isupdated, setModifiedId, Email }) {

  const [dataset, setDataset] = useState([]);
  let [modalOpen, setModalOpen] = useState(false);
  let [modaldata, setModaldata] = useState();

  useEffect(() => {
    getData();
  }, [isupdated]);

  // PNG Download
  const [png, ref] = useRechartToPng();
  const handleDownload = React.useCallback(async () => {
    saveAs(png, "myChart.png");
  }, [png]);
  // PNG Download

  // Fetching data from the server
  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { setDataset(data); setModifiedId(); }) // Set fetched data to an array
      .catch(error => console.error('Error:', error))
  }

  async function removeObs(obs) {
    await fetch('http://localhost:3000/' + Email + '/' + obs.id, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(() => { getData(); }) // Refresh Array when obs deleted
      .catch(error => console.error("error: " + error))
  }

  async function openObs(id) {
    await fetch('http://localhost:3000/' + Email + '/' + id,)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { openModal(data) }) // Set fetched data to an array
      .catch(error => console.error('Error:', error))
  }

  function openModal(data) {
    setModalOpen(true);
    fillmodaldata(data);
  }

  function fillmodaldata(data) {
    setModaldata(
      <p>
            <b>Havainto numero:</b> {data.id}.<br></br><br/>
            Nukkumaan käyty: {moment(data.tosleep).format("DD.MM.YYYY HH:mm")}.<br></br><br/>
            Herätty: {moment(data.fromsleep).format("DD.MM.YYYY HH:mm")}. <br></br><br/>
            Nukuttu aika tunteina: {data.totalHours}. <br></br><br/>
            Koettu unen laatu: {data.range}. <br></br><br/>
            Kuvaus unen vaikutuksista päivään: {data.desc}<br></br><br/>
      </p>

    )
  }

  function closeModal() {
    setModalOpen(false);
  }

  const formattosleep = (tickItem) => {
    return moment(tickItem).format("DD.MM");
  }

  const formathours = (tickItem) => {
    return tickItem+'h';
  }


  return (
    <Tabs className="box">
      <TabList>
        <Tab>Taulukko</Tab>
        <Tab>Graafi</Tab>
      </TabList>

      <TabPanel>
        <table>
          <thead>
            <tr>
              <th></th>
              <th>Havainto nro:</th>
              <th>Nukkumaan</th>
              <th>Herätty</th>
              <th>Nukuttu aika</th>
              <th>Unen laatu</th>              
            </tr>
          </thead>
          <tbody>
            {dataset.map((c, index) =>
              <tr key={c.id}>
                <td><button onClick={() => openObs(c.id)}>Avaa</button></td>
                <td>{c.id}</td>
                <td>{moment(c.tosleep).format("DD.MM.YYYY HH:mm")}</td>
                <td>{moment(c.fromsleep).format("DD.MM.YYYY HH:mm")}</td>
                <td>{c.totalHours}</td>
                <td>{c.range}</td>
                <td>
                  <button onClick={() => setModifiedId(c.id)}><FaEdit/></button>
                </td>
                <td><button onClick={() => removeObs(c)}><FaTrashAlt/></button></td>
              </tr>)}
          </tbody>
        </table>
      </TabPanel>

      <TabPanel>
        <BarChart width={450} height={250} data={dataset} ref={ref} className="barchart">
          <CartesianGrid />
          <XAxis dataKey="tosleep" tickFormatter={formattosleep} />
          <YAxis tickFormatter={formathours}/>
          <Tooltip/>
          <Legend/>
          <Bar dataKey="totalHours" name="Nukuttu aika tunteina" fill="#8884d8" />
          <Bar dataKey="range" name="Unen laatu" fill="#82ca9d" />
        </BarChart>
        <button onClick={handleDownload} className="pngbutton">Lataa kuvio png-tiedostona</button>
      </TabPanel>

      <Modal  ariaHideApp={false} isOpen={modalOpen} onRequestClose={closeModal} contentLabel="Modal" className="modal">
        {modaldata}
        <button onClick={closeModal}>close</button>
      </Modal>

    </Tabs>
  );
}



export default Databox;
