import React, { useState } from 'react';
import './content.css';

import DatePicker from 'react-datepicker';
import { registerLocale } from "react-datepicker";
import fi from 'date-fns/locale/fi';
import "react-datepicker/dist/react-datepicker.css";

import moment from 'moment';

import InputRange from 'react-input-range';
import "react-input-range/lib/css/index.css";

import { useForm } from "react-hook-form";

registerLocale('fi', fi);



function Formbox() {
  const { register, handleSubmit, watch, errors } = useForm();


  //Formissa kerättävä data:
  let [rangevalue, setRangevalue] = useState(1);

  let [tosleepvalue, setTosleepvalue] = useState(new Date()); //Tallentuu muodossa 00:00:00
  let formattedtosleepvalue = moment(tosleepvalue).format("DD.MM.YYYY HH:mm");

  let [fromsleepvalue, setFromsleepvalue] = useState(new Date()); //Tallentuu muodossa 00:00:00

  let [desc, setDesc] = useState();

  // Nukkumiseen käytetty aika selvitettävä, jotta voi
  let totalsleeptime = String(fromsleepvalue - tosleepvalue);


  const onSubmit = data => {
    console.log('submittia = ' +JSON.stringify(data));    
    console.log(data.tosleep);
  }

  return (
    <div className="box">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Kävin nukkumaan:</label>
        <DatePicker
          selected={tosleepvalue}          
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"  
          locale="fi"        
          timeIntervals={15}          
          onChange={e => setTosleepvalue(e)}    
          ref={register({ name: 'tosleepvalue', value: formattedtosleepvalue })}      
        />

        <label>Heräsin:</label>
        <DatePicker
          selected={fromsleepvalue}  
          showTimeSelect          
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"          
          timeIntervals={15}
          onChange={e => setFromsleepvalue(e)}  
          ref={register({ name: 'fromleepvalue', value: fromsleepvalue })}  
        />
        <br></br>
        <label>Unen laatu:</label>


        <InputRange 
          name="range"
          maxValue={5}
          minValue={1}
          value={rangevalue}
          onChange={e => setRangevalue(e)}
        />

        <label>Sanallinen arvio:</label><br></br>
        <textarea name="desc" onChange={(e)=>setDesc(e)}></textarea>

        <button type="submit">Tallenna</button>    

      </form>


    </div>
  );
}

export default Formbox;
