import React, { useState } from 'react';
import './content.css';

import DatePicker, { getDefaultLocale } from 'react-datepicker'; 
import "react-datepicker/dist/react-datepicker.css";
import { registerLocale } from "react-datepicker";

import moment from 'moment'; // Module for date formatting

import InputRange from 'react-input-range'; // Range module for the slide input
import "react-input-range/lib/css/index.css";

import { useForm } from "react-hook-form"; // Form validation module

import fi from 'date-fns/locale/fi'; //Finnish formatting for date and time
registerLocale('fi', fi);

/**
Naming policy:

Email
password
id
toSleep
fromSleep
totalHours
range 
desc

*/

/*
Simple REST API with Node.js json-server to read and write a local JSON file.
json-server startup:
json-server --watch obs.json

*/

function Formbox({getData}) {
  const { register, handleSubmit, watch, errors, reset } = useForm();

  //Data collected on the form:

  var yesterday = new Date();  // Initializing tosleep value to last night
  yesterday.setDate(yesterday.getDate()-1);
  yesterday.setHours(20);

  let [tosleep, setTosleep] = useState(yesterday);
  let formattedtosleep = moment(tosleep).format("DD.MM.YYYY HH:mm");

  let today = new Date(); // Initializing fromsleep value to this morning
  today.setHours(7);
  let [fromsleep, setFromsleep] = useState(today);
  let formattedfromsleep = moment(fromsleep).format("DD.MM.YYYY HH:mm");

  let [range, setRange] = useState(1);
  let [desc, setDesc] = useState('');

  let [Email, setEmail] = useState('eerokos');

  // Calculating total sleeping hours  
  var totalHours = moment.utc(moment(fromsleep, "HH:mm:ss").diff(moment(tosleep, "HH:mm:ss"))).format("HH:mm")

  // When save-button is pressed we format the data  and post it to the server
  const onSubmit = data => {
    data.tosleep = formattedtosleep;
    data.fromsleep = formattedfromsleep;
    data.range = range;
    data.desc = desc;
    data.totalHours = totalHours;
    console.log('submitted data = ' + JSON.stringify(data));

    addObs(data);
    async function addObs(data) {
      await fetch('http://localhost:3000/' + Email, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      })
        .then(() => { formreset(); {getData;}  })
        .catch(error => console.error("error: " + error))
    }
  }

  let formreset = () => {
    setTosleep(yesterday);
    setFromsleep(today);
    setRange(1);
    setDesc('');  
  }

  return (
    <div className="box">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Kävin nukkumaan:</label>
        <DatePicker
          selected={tosleep}
          onChange={e => setTosleep(e)}
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"
          timeIntervals={15}
          ref={register({ name: 'tosleep', value: formattedtosleep })}
        />

        <label>Heräsin:</label>
        <DatePicker
          selected={fromsleep}
          onChange={e => setFromsleep(e)}
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"
          timeIntervals={15}
          ref={register({ name: 'fromsleep', value: formattedfromsleep })}
        />
        <br></br>
        <label>Unen laatu:</label>
        <InputRange
          value={range}
          name="range"
          onChange={e => setRange(e)}
          maxValue={5}
          minValue={1}
          ref={register({ name: 'range', value: range })}
        />

        <label>Sanallinen arvio:</label><br></br>
        <textarea
          name="desc"
          onChange={(e) => setDesc(e.target.value)}
          ref={register({ name: 'desc', value: desc })}
        ></textarea>

        <button type="submit">Tallenna</button>

      </form>

    </div>
  );
}

export default Formbox;
