import React, { useState } from 'react';
import './content.css';

import DatePicker from 'react-datepicker';
import { registerLocale } from "react-datepicker";
import fi from 'date-fns/locale/fi';
import "react-datepicker/dist/react-datepicker.css";

import InputRange from 'react-input-range';
import "react-input-range/lib/css/index.css";

import { useForm } from "react-hook-form";

registerLocale('fi', fi);



function Formbox() {
  //Formissa kerättävä data:
  const [rangevalue, setRangevalue] = useState(1);
  const [tosleepvalue, setTosleepvalue] = useState(); //Tallentuu muodossa 00:00:00
  const [fromsleepvalue, setFromsleepvalue] = useState(); //Tallentuu muodossa 00:00:00
  const [date, setDate] = useState();
  const [desc, setDesc] = useState();

  // Nukkumiseen käytetty aika selvitettävä, jotta voi
  const totalsleeptime = fromsleepvalue - tosleepvalue;

  function test(){    
    console.log('to =' +tosleepvalue);
    console.log('from =' +fromsleepvalue);
    console.log(totalsleeptime);
  }

  const onSubmit = data => {
    console.log('submittia' +data);
  }

  return (
    <div className="box">
      <form onSubmit={onSubmit()}>
        <label>Kävin nukkumaan:</label>
        <DatePicker
          showTimeSelect
          timeFormat="HH:mm"
          dateFormat="Pp"  
          locale="fi"        
          timeIntervals={15}
          selected={tosleepvalue}
          onChange={e => setTosleepvalue(e)}          
        />

        <label>Heräsin:</label>
        <DatePicker
          showTimeSelect          
          timeFormat="HH:mm"
          dateFormat="Pp"
          locale="fi"
          timeIntervals={15}
          selected={fromsleepvalue}          
          onChange={e => setFromsleepvalue(e)}  
        />
        <br></br>
        <label>Unen laatu:</label>


        <InputRange
          maxValue={5}
          minValue={1}
          value={rangevalue}
          onChange={e => setRangevalue(e)}
        />

        <label>Sanallinen arvio:</label><br></br>
        <textarea onChange={(e)=>setDesc(e)}></textarea>


      </form>
      <button onClick={test()}>Tallenna</button>  

    </div>
  );
}

export default Formbox;
