import React, { useState } from 'react';
import './content.css';

import DatePicker from 'react-datepicker';
import { registerLocale } from "react-datepicker";
import fi from 'date-fns/locale/fi';
import "react-datepicker/dist/react-datepicker.css";

import moment from 'moment';

import InputRange from 'react-input-range';
import "react-input-range/lib/css/index.css";

import { useForm } from "react-hook-form";

registerLocale('fi', fi);

/**
Naming policy:

Email
password
ObsId
toSleep
fromSleep
totalHours
range 
desc

*/

/*
Node.js json-server to read and write a local JSON file
*/

function Formbox() {
  const { register, handleSubmit, watch, errors } = useForm();

  //Data collected on the form:
  let [tosleep, setTosleep] = useState(new Date());
  let formattedtosleep = moment(tosleep).format("DD.MM.YYYY HH:mm");

  let [fromsleep, setFromsleep] = useState(new Date());
  let formattedfromsleep = moment(fromsleep).format("DD.MM.YYYY HH:mm");
  let [range, setRange] = useState(1);
  let [desc, setDesc] = useState('');

  let [Email, setEmail] = useState('eerokos');

  // total sleeping hours
  let totalHours = String(fromsleep - tosleep);

  const onSubmit = data => {
    data.tosleep = formattedtosleep;
    data.fromsleep = formattedfromsleep;
    data.range = range;
    data.desc = desc;
    console.log('submittia = ' + JSON.stringify(data));

    const axios = require('axios');

    axios.put('http://localhost:3000/eerokos', {
      data
    }).then(resp => {

      console.log(resp.data);
    }).catch(error => {

      console.log(error);
    });
  }
    return (
      <div className="box">
        <form onSubmit={handleSubmit(onSubmit)}>
          <label>Kävin nukkumaan:</label>
          <DatePicker
            selected={tosleep}
            onChange={e => setTosleep(e)}
            showTimeSelect
            timeFormat="HH:mm"
            dateFormat="Pp"
            locale="fi"
            timeIntervals={15}
            ref={register({ name: 'tosleep', value: formattedtosleep })}
          />

          <label>Heräsin:</label>
          <DatePicker
            selected={fromsleep}
            onChange={e => setFromsleep(e)}
            showTimeSelect
            timeFormat="HH:mm"
            dateFormat="Pp"
            locale="fi"
            timeIntervals={15}
            ref={register({ name: 'fromsleep', value: formattedfromsleep })}
          />
          <br></br>
          <label>Unen laatu:</label>
          <InputRange
            value={range}
            name="range"
            onChange={e => setRange(e)}
            maxValue={5}
            minValue={1}
            ref={register({ name: 'range', value: range })}
          />

          <label>Sanallinen arvio:</label><br></br>
          <textarea
            name="desc"
            onChange={(e) => setDesc(e.target.value)}
            ref={register({ name: 'desc', value: desc })}
          ></textarea>

          <button type="submit">Tallenna</button>

        </form>


      </div>
    );
  }

  export default Formbox;
