  // Visual appearance

  if(!token){
    return <Login/>
  }

  return (
    <div className="container">
      <div className="header">
        <h4>Kansanterveystieteen perusteet -Unipäiväkirja</h4>
        <button>Kirjaudu ulos</button>  
      </div>

      <div className="content">
        <Formbox setIsupdated={setIsupdated} modifiedId={modifiedId}/>
        <Databox isupdated={isupdated} setModifiedId={setModifiedId} />
      </div>

      <div className="footer"><h5>Footer</h5></div>
    </div>
  );
}

export default App;
