import React, { useState, useEffect } from 'react';
import './content.css';

// PNG DOWNLOAD
import { useRechartToPng } from "recharts-to-png";
import { saveAs } from 'file-saver';
// PNG DOWNLOAD

import Modal from 'react-modal';

// React-tabs module
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

import moment from 'moment'; // Module for date formatting

// Recharts.js module
import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Bar, Legend } from 'recharts';

function Databox({ isupdated, setModifiedId, Email }) {

  const [dataset, setDataset] = useState([]);
  let [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    getData();
  }, [isupdated]);

  // PNG Download
  const [png, ref] = useRechartToPng();
  const handleDownload = React.useCallback(async () => {
    saveAs(png, "myChart.png");
  }, [png]);
  // PNG Download

  // Fetching data from the server
  async function getData() {
    fetch('http://localhost:3000/' + Email)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { setDataset(data); setModifiedId(); }) // Set fetched data to an array
      .catch(error => console.error('Error:', error))
  }

  async function removeObs(obs) {
    await fetch('http://localhost:3000/' + Email + '/' + obs.id, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(() => { getData(); }) // Refresh Array when obs deleted
      .catch(error => console.error("error: " + error))
  }

  async function openObs(id) {
    await fetch('http://localhost:3000/' + Email + '/' + id,)
      .then(res => {
        if (res.status === 200) {
          return res.json();
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .then(data => { openModal(JSON.stringify(data)) }) // Set fetched data to an array
      .catch(error => console.error('Error:', error))
  }

  function openModal(data){    
    setModalOpen(true);
    alert(data);
  }

  function closeModal(){
    setModalOpen(false);
  }

  return (
    <Tabs className="box">
      <TabList>
        <Tab>Array</Tab>
        <Tab>Graph</Tab>
      </TabList>

      <TabPanel>
        <table>
          <thead>
            <tr>
              <th>Havainto nro:</th>
              <th>Nukkumaan</th>
              <th>Herätty</th>
              <th>Nukuttu aika</th>
              <th>Unen laatu</th>
            </tr>
          </thead>
          <tbody>
            {dataset.map((c, index) =>
              <tr key={c.id}>
                <td>{c.id}</td>
                <td>{moment(c.tosleep).format("DD.MM.YYYY HH:mm")}</td>
                <td>{moment(c.fromsleep).format("DD.MM.YYYY HH:mm")}</td>
                <td>{c.totalHours}</td>
                <td>{c.range}</td>
                <td>{c.desc.slice(0, 20)}...</td>
                <td><button onClick={() => openObs(c.id)}>Avaa</button></td>
                <td><button onClick={() => setModifiedId(c.id)}>Muokkaa</button></td>
                <td><button onClick={() => removeObs(c)}>Poista</button></td>
              </tr>)}
          </tbody>
        </table>
      </TabPanel>

      <TabPanel>
        <BarChart width={450} height={250} data={dataset} ref={ref}>
          <CartesianGrid />
          <XAxis dataKey="id" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="totalHours" name="Nukuttu aika tunteina" fill="#8884d8" />
          <Bar dataKey="range" name="Unen laatu" fill="#82ca9d" />
        </BarChart>
        <button onClick={handleDownload}>Lataa kuvio png-tiedostona</button>
      </TabPanel>

      <Modal
          isOpen={modaOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <h2 ref={_subtitle => (subtitle = _subtitle)}>Hello</h2>
          <button onClick={closeModal}>close</button>
          <div>I am a modal</div>
          <form>
            <input />
            <button>tab navigation</button>
            <button>stays</button>
            <button>inside</button>
            <button>the modal</button>
          </form>
      </Modal>

    </Tabs>
  );
}



export default Databox;
